package sec02;

public class OperatorTest1 {

	// 산술 연산자 실습
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 3;

		int c = a + b;
		int d = a - b;
		int e = a / b;
		int f = a % b;

		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);

	}

}
