package sec02;

public class OperatorTest3 {

	// 비교 연산자 실습
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		
		boolean b = a > 10;
		boolean c = a != 10;
		boolean d = a >= 10;
		boolean e = a == 10;
		
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);

	}

}
